import streamlit as st 

# Layout 
st.title("Streamlit Layouts")
col1,col2= st.columns(2)

with col1:
    one=st.button("Button One")
    st.image("data:image/jpeg;base64,",width=200)
    if one:
        st.write("You clicked Button One")
with col2:
    two=st.button("Button Two")
    st.image("data:image/jpeg;base64,",width=200)
    if two:
        st.write("You clicked Button Two")        


st.sidebar.title("This is a sidebar")
st.sidebar.button("Sidebar Button")
name=st.sidebar.text_input("Enter your name","Type here ...")

st.write(f"Hello {name}")

with st.expander("Expand me"):
    
    st.write("""
              1. Create a Streamlit app.
              2. Import the Streamlit library.
              3. Create a title for your app.
              4. Create a sidebar for your app.
              5. Run your app.
             """)
    

st.markdown(" # Heading 1 ")
st.markdown('> Blockquote')    